mapper.select:
select *
from member mem,
member_auth m_auth
where
mem.userid=m_auth.userid
and mem.mno = #{mno}

mapper.update:
update member mem ,
member_auth m_auth
set mem.approval_status = #{approval_status} ,
m_auth.authority =#{authority}
where mem.mno = #{mno}
and m_auth.userid=#{userid}
