mapper.select:
SELECT bno, title, content, writer, regdate, viewcnt, replycnt, grpno, step, depth
FROM tbl_board
WHERE bno = #{bno}

mapper.update:
UPDATE tbl_board
SET title = #{title}, content = #{content}
WHERE bno = #{bno}

UPDATE tbl_board 
SET replycnt = replycnt + #{amount} 
WHERE bno = #{bno}

UPDATE tbl_board
SET viewcnt = viewcnt + 1 
WHERE bno = #{bno}

mapper.select:
SELECT fullName FROM tbl_attach
WHERE bno = #{bno}
ORDER BY regdate;
