mapper.update:
update board set
title=#{title}, content=#{content}
where bno = #{bno}

mapper.select:

	SELECT *
	FROM board
	WHERE bno > 0 and btype = #{btype}

	
	ORDER BY grpno DESC, step ASC
	LIMIT #{pageStart}, ${perPageNum}

mapper.update:
UPDATE board
SET replycnt = replycnt + #{amount}
WHERE
bno = #{bno}

UPDATE board
SET hit = hit + 1
WHERE bno = #{bno}

mapper.select:
SELECT filename FROM
attachfile
WHERE bno = #{bno}
ORDER BY reg_date;
